#!/bin/bash

echo \"üöÄ WagerGPT Setup Script\"\necho \"========================\"\n\n# Check if Flutter is installed\nif ! command -v flutter &> /dev/null; then\n    echo \"‚ùå Flutter is not installed. Please install Flutter first.\"\n    exit 1\nfi\n\necho \"‚úÖ Flutter found\"\n\n# Navigate to project directory\nif [ ! -f \"pubspec.yaml\" ]; then\n    echo \"‚ùå Not in a Flutter project directory. Please run this script from the project root.\"\n    exit 1\nfi\n\necho \"‚úÖ Flutter project detected\"\n\n# Install dependencies\necho \"üì¶ Installing dependencies...\"\nflutter pub get\n\nif [ $? -eq 0 ]; then\n    echo \"‚úÖ Dependencies installed successfully\"\nelse\n    echo \"‚ùå Failed to install dependencies\"\n    exit 1\nfi\n\n# Check if .env file exists\nif [ ! -f \"assets/.env\" ]; then\n    echo \"‚ùå assets/.env file not found\"\n    exit 1\nfi\n\necho \"‚úÖ Environment file found\"\n\n# Check if Gemini API key is configured\nif grep -q \"YOUR_GEMINI_API_KEY_HERE\" assets/.env; then\n    echo \"‚ö†Ô∏è  WARNING: Gemini API key not configured!\"\n    echo \"\"\n    echo \"To complete the setup:\"\n    echo \"1. Get your Gemini API key from: https://makersuite.google.com/app/apikey\"\n    echo \"2. Open assets/.env file\"\n    echo \"3. Replace YOUR_GEMINI_API_KEY_HERE with your actual API key\"\n    echo \"\"\n    echo \"Example:\"\n    echo \"GEMINI_API_KEY=your_actual_api_key_here\"\nelse\n    echo \"‚úÖ Gemini API key appears to be configured\"\nfi\n\necho \"\"\necho \"üéâ Setup complete!\"\necho \"\"\necho \"To test WagerGPT:\"\necho \"1. Run: flutter run\"\necho \"2. Navigate to Discover page\"\necho \"3. Tap on WagerGPT under Features\"\necho \"4. Start chatting with your AI betting assistant!\"\necho \"\"\necho \"üìö For more information, see WAGERGPT_IMPLEMENTATION.md\"\n